package ferrum

import (
	"testing"

	"github.com/stretchr/testify/assert"
)

func TestSubstringCalculator(t *testing.T) {
	// Write your code here
	assert.Equal(t, int64(15), substringCalculator("abcde"))
	assert.Equal(t, int64(53), substringCalculator("kincenvizh"))
	assert.Equal(t, int64(1), substringCalculator("a"))
	assert.Equal(t, int64(3), substringCalculator("ab"))
	assert.Equal(t, int64(499011), substringCalculator(""))
	assert.Equal(t, int64(557435), substringCalculator(""))
}
